// init
if(!$.browser.msie || $.browser.version >= 8) {
    $(document).ready(function(){

        // apply to textareas
        $('textarea.codemirror').each(function(){

            var textarea = $(this);
            var width = textarea.width(), height = textarea.height();
            var mode;

            if(textarea.hasClass('codemirror-xml')) mode = 'application/xml';
            else if(textarea.hasClass('codemirror-html')) mode = {name: 'hcm', backdrop: 'text/html'};
            else if(textarea.hasClass('codemirror-html-nohcm')) mode = 'text/html';
            else if(textarea.hasClass('codemirror-css')) mode = 'text/css';
            else if(textarea.hasClass('codemirror-js')) mode = 'text/javascript';
            else if(textarea.hasClass('codemirror-php')) mode = 'application/x-httpd-php';
            else mode = {name: 'hcm', backdrop: 'text/html'};

            var editor = CodeMirror.fromTextArea(this, {
                mode: mode,
                theme: sl_scheme_dark ? 'ambiance' : 'eclipse',
                lineWrapping: true,
                lineNumbers: true,
                smartIndent: false,
                electricChars: false,
                tabSize: 4,
                indentWithTabs: false,
                extraKeys: {Tab: function(cm){
                    cm.replaceSelection("    ", "end");
                }}
            });

            $(editor.getScrollerElement()).css({height: height+'px'});
            $(editor.getWrapperElement()).css({width: width+'px'});
            editor.refresh();

        });
    });
}